const request = require('request');
const tjs = require('./typeof');

module.exports = (gameid, callback) => {
  if(tjs(gameid)!== undefined){
    request({
      uri: '/appdetails',
      baseUrl: 'http://store.steampowered.com/api',
      qs: {
        appids : gameid,
		cc : 'us',
		l : 'en'
      }
    }, (err, res, body) => {
      if(err) callback("There was an error making your request.");
      callback(null, body);
    });
  }
}
